import React, { useState, useEffect } from 'react';
import { useLoaderData, Link } from 'react-router-dom';

/*
ITP 404, Final
Author: Carson Wang
Email: carsonw@usc.edu
*/

export default function SavedResorts() {
    const resortList = useLoaderData();
    const [savedResorts, setSavedResorts] = useState([]);

    useEffect(() => {
        const sortedResorts = resortList.sort((a, b) => a.Resort.localeCompare(b.Resort));
        setSavedResorts(sortedResorts);
    }, [resortList]);

    const [isModalOpen, setModalOpen] = useState(false);
    const [selectedResortId, setSelectedResortId] = useState(null);

    // ... other state and functions ...

    const handleDelete = (resortId) => {
        fetch(`http://localhost:3000/saved/${resortId}`, { method: 'DELETE' })
            .then(() => {
                setSavedResorts(savedResorts.filter(resort => resort.id !== resortId));
                setModalOpen(false); // Close the modal on successful deletion
            })
            .catch(error => {
                console.error('Error deleting resort:', error);
                setModalOpen(false); // Close the modal on error
            });
    };

    const openDeleteModal = (resortId) => {
        setSelectedResortId(resortId);
        setModalOpen(true);
    };

    <ModalDialog
        isOpen={isModalOpen}
        onClose={() => setModalOpen(false)}
        title="Confirm Delete"
        onAction={() => handleDelete(selectedResortId)}
        actionSuccessMessage="Resort successfully deleted."
    >
        <p>Are you sure you want to delete this resort?</p>
    </ModalDialog>


    // const handleDelete = (resortId) => {
    //     fetch(`http://localhost:3000/saved/${resortId}`, { method: 'DELETE' })
    //         .then(() => {
    //             setSavedResorts(savedResorts.filter(resort => resort.id !== resortId));
    //         })
    //         .catch(error => console.error('Error deleting resort:', error));
    // };

    const width = "18rem";
    return (
        <div className='page'>
            <h4>Saved Resorts</h4>
            <hr />
            {
                savedResorts.length > 0 ? (
                    <div class="card" style={{ width: width }}>
                        {savedResorts.map((resort, index) => (
                            <ul key={index} class="list-group list-group-flush">
                                <li class="list-group-item">
                                    <h6>{resort.Resort} in {resort['Location (State)']}</h6>
                                    <a class="card-link"><button class="btn btn-light mb-3"><Link to={`/resort-details/${resort.Resort}`}>Detail</Link></button></a>
                                    <a class="card-link"><button class="btn btn-light mb-3" onClick={() => openDeleteModal(resort.id)}>Delete</button></a>
                                </li>
                            </ul>
                        ))}
                    </div>) : (
                    <p>You have not saved any resort yet.</p>
                )
            }
        </div>
    );
}
