import { createBrowserRouter, RouterProvider } from 'react-router-dom';
import React from 'react';
import ReactDOM from 'react-dom/client';

// Importing route components
import Root from './routes/root';
import DetailPage from './routes/details';
import FAQPagel from './routes/userFAQ';
import Index from './routes/index';
import Search from './routes/search';

import './css/index.css';

/*
ITP 404, Midterm
Author: Carson Wang
Email: carsonw@usc.edu
Create router, loader for react-router-dom
*/


// Create the router with route configurations
const router = createBrowserRouter([
  {
    path: '/',
    element: <Root />,
    children: [
      {
        path: '/',
        element: <Index />,
      },
      {
        path: '/SkiResorts',
        element: <DetailPage />,
        loader: async () => {
          try {
            const response = await fetch('https://ski-resorts-and-conditions.p.rapidapi.com/v1/resort/whistler-blackcomb', {
              method: 'GET',
              headers: {
                'X-RapidAPI-Key': '55989d32a6mshcc21389f5a6e365p1f7963jsn4be692249f51',
                'X-RapidAPI-Host': 'ski-resorts-and-conditions.p.rapidapi.com'
              }
            });
            if (!response.ok) {
              throw new Error('Network response was not ok');
            }
            return response.json();
          } catch (error) {
            // Handle the error more gracefully, possibly by throwing to an error boundary or returning an error state
            throw error;
          }
        },
      },
    ]
  }
]);

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <RouterProvider router={router} />
  </React.StrictMode>
);
