import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

export default function TripForm() {
    const [resortList, setResortList] = useState([]);
    const [year, setYear] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [resortName, setResortName] = useState('');
    const [note, setNote] = useState('');
    const [submitted, setSubmitted] = useState(false);

    useEffect(() => {
        fetch('http://localhost:3000/resorts')
            .then(response => response.json())
            .then(data => {
                setResortList(data.Resort);
            })
            .catch(error => console.error('Error fetching resorts:', error));
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();

        const newTrip = {
            id_year: year,
            details: [{
                "date-start": startDate,
                "date-end": endDate,
                resortName,
                pass: resortList.find(resort => resort.name === resortName)?.pass || 'Unknown',
                note
            }]
        };

        try {
            await fetch('http://localhost:3000/trips', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(newTrip)
            });
            setSubmitted(true);
        } catch (error) {
            console.error('Error submitting trip:', error);
            // Handle error here
        }
    };

    if (submitted) {
        return (
            <div>
                <p>You have successfully submitted the form!</p>
                <button onClick={() => setSubmitted(false)}>Add another one?</button>
                <Link to="/my-trips">View my trips</Link>
            </div>
        );
    }

    return (
        <form onSubmit={handleSubmit}>
            {/* Year */}
            <label>
                Year:
                <input type="number" value={year} onChange={(e) => setYear(e.target.value)} required />
            </label>
            {/* Start Date */}
            <label>
                Start Date:
                <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} required />
            </label>
            {/* End Date */}
            <label>
                End Date:
                <input type="date" value={endDate} min={startDate} onChange={(e) => setEndDate(e.target.value)} required />
            </label>
            {/* Select Resort Name */}
            <label>
                Select Resort Name:
                <select value={resortName} onChange={(e) => setResortName(e.target.value)} required>
                    <option value="">Select a resort</option>
                    {resortList.map((resort, index) => (
                        <option key={index} value={resort.name}>{resort.name}</option>
                    ))}
                </select>
            </label>
            {/* Notes */}
            <label>
                Notes:
                <textarea value={note} onChange={(e) => setNote(e.target.value)}></textarea>
            </label>
            {/* Submit Button */}
            <button type="submit">Submit</button>
        </form>
    );
};
