import { useState } from 'react';
import { useLoaderData } from 'react-router-dom';
import React from 'react';

/*
ITP 404, Midterm
Author: Carson Wang
Email: carsonw@usc.edu
This file load the details for the meal that user selected;
Allow user to load comments/ add comments (including error check)
*/

export default function details() {
  // Fetch the meal data using react-router's useLoaderData hook
  const resortList = useLoaderData();

  const [resorts, setResors] = useState(resortList);

  return (
    <div className="page">
      <h1>Ski Resorts</h1>
      <ul>
        {resorts.map((resort) => (
          <li key={resort.slug}>
            <h2>{resort.name}</h2>
            <p>{resort.region}, {resort.country}</p>
            <a href={resort.url} target="_blank" rel="noopener noreferrer">View More</a>
          </li>
        ))}
      </ul>
    </div>
  );
}
