import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

export default function TripForm() {
    const [resortList, setResortList] = useState([]);
    const [year, setYear] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [resortName, setResortName] = useState('');
    const [note, setNote] = useState('');
    const [submitted, setSubmitted] = useState(false);

    useEffect(() => {
        fetch('http://localhost:3000/resorts')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                console.log(data); // Log to see the structure of the fetched data
                setResortList(data); // Adjust this line if the structure is different
                console.log(resortList)
            })
            .catch(error => {
                console.error('Error fetching resorts:', error);
            });
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setSubmitting(true);
        setError('');

        const newTripDetail = {
            "date-start": startDate,
            "date-end": endDate,
            resortName,
            pass: resortList.find(resort => resort.Resort === resortName)?.['Ski Pass'] || 'Unknown',
            note
        };

        try {
            // First, check if the year already exists
            const response = await fetch(`http://localhost:3000/trips?id_year=${year}`);
            const data = await response.json();

            if (data.length > 0) {
                // Year exists, update the existing entry
                const existingEntry = data[0];
                existingEntry.details.push(newTripDetail);

                await fetch(`http://localhost:3000/trips/${existingEntry.id}`, {
                    method: 'PUT',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(existingEntry)
                });
            } else {
                // Year does not exist, create a new entry
                const newEntry = {
                    id_year: year,
                    details: [newTripDetail]
                };

                await fetch('http://localhost:3000/trips', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(newEntry)
                });
            }

            setSubmitted(true);
            // Reset form fields here if necessary
        } catch (error) {
            console.error('Error:', error);
            setError('An error occurred while submitting the trip.');
        } finally {
            setSubmitting(false);
        }
    };

    if (submitted) {
        return (
            <div>
                <p>You have successfully submitted the form!</p>
                <button onClick={() => setSubmitted(false)}>Add another one?</button>
                <Link to="/my-trips">View my trips</Link>
            </div>
        );
    }

    return (
        <form onSubmit={handleSubmit}>
            {/* Year */}
            <label>
                Year:
                <input type="number" value={year} onChange={(e) => setYear(e.target.value)} required />
            </label>
            {/* Start Date */}
            <label>
                Start Date:
                <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} required />
            </label>
            {/* End Date */}
            <label>
                End Date:
                <input type="date" value={endDate} min={startDate} onChange={(e) => setEndDate(e.target.value)} required />
            </label>
            {/* Select Resort Name */}
            <label>
                Select Resort Name:
                <select value={resortName} onChange={(e) => setResortName(e.target.value)} required>
                    <option value="">Select a resort</option>
                    {resortList.map((resort, index) => (
                        <option key={index} value={resort.Resort}>{resort.Resort}</option>
                    ))}
                </select>
            </label>
            {/* Notes */}
            <label>
                Notes:
                <textarea value={note} onChange={(e) => setNote(e.target.value)}></textarea>
            </label>
            {/* Submit Button */}
            <button type="submit">Submit</button>
        </form>
    );
};
