import React from 'react';
import { render, fireEvent } from '@testing-library/react';
import Comment from './Comment'; // Adjust this import based on your file structure and export type

test('renders input fields for comment submission', () => {
  const { getByTestId } = render(<Comment />);

  expect(getByTestId('Name')).toBeInTheDocument();
  expect(getByTestId('Rating')).toBeInTheDocument();
  expect(getByTestId('Trip-Notes')).toBeInTheDocument();
  expect(getByTestId('Submit-Review')).toBeInTheDocument();
});

test('handles input changes', () => {
  const { getByTestId } = render(<Comment />);

  const nameInput = getByTestId('Name');
  fireEvent.change(nameInput, { target: { value: 'John Doe' } });
  expect(nameInput.value).toBe('John Doe');

  const ratingInput = getByTestId('Rating');
  fireEvent.change(ratingInput, { target: { value: '4' } });
  expect(ratingInput.value).toBe('4');

  const commentTextarea = getByTestId('Trip-Notes');
  fireEvent.change(commentTextarea, { target: { value: 'Great trip!' } });
  expect(commentTextarea.value).toBe('Great trip!');
});

test('submits the form', () => {
  const handleSubmit = jest.fn(e => e.preventDefault());
  const { getByTestId } = render(<Comment handleSubmit={handleSubmit} />);

  fireEvent.change(getByTestId('Name'), { target: { value: 'John Doe' } });
  fireEvent.change(getByTestId('Rating'), { target: { value: '4' } });
  fireEvent.change(getByTestId('Trip-Notes'), { target: { value: 'Great trip!' } });
  fireEvent.click(getByTestId('Submit-Review'));

  expect(handleSubmit).toHaveBeenCalled();
});
