import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

export default function TripForm() {
    const [resortList, setResortList] = useState([]);
    const [year, setYear] = useState('2023');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [resortName, setResortName] = useState('');
    const [note, setNote] = useState('');
    const [submitted, setSubmitted] = useState(false);
    const [error, setError] = useState(''); // Error state

    useEffect(() => {
        fetch('http://localhost:3000/resorts')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                setResortList(data);
            })
            .catch(error => {
                console.error('Error fetching resorts:', error);
                setError('Failed to load resorts.'); // Set error message
            });
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(''); // Clear previous error

        const newTripDetail = {
            "date-start": startDate,
            "date-end": endDate,
            resortName,
            pass: resortList.find(resort => resort.Resort === resortName)?.['Ski Pass'] || 'Unknown',
            note
        };

        try {
            const response = await fetch(`http://localhost:3000/trips?id_year=${year}`);
            const data = await response.json();

            if (data.length > 0) {
                const existingEntry = data[0];
                existingEntry.details.push(newTripDetail);

                await fetch(`http://localhost:3000/trips/${existingEntry.id}`, {
                    method: 'PUT',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(existingEntry)
                });
            } else {
                const newEntry = {
                    id_year: year,
                    details: [newTripDetail]
                };

                await fetch('http://localhost:3000/trips', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(newEntry)
                });
            }

            // Reset form fields
            setYear('');
            setStartDate('');
            setEndDate('');
            setResortName('');
            setNote('');
            setSubmitted(true);
        } catch (error) {
            console.error('Error:', error);
            setError('An error occurred while submitting the trip.');
        }
    };

    if (submitted) {
        return (
            <div>
                <p>You have successfully submitted the form!</p>
                <button onClick={() => { setSubmitted(false); setError(''); }}>Add another one?</button>
            </div>
        );
    }

    return (
        <form onSubmit={handleSubmit}>
            {/* Year */}
            <label>
                Year:
                <input type="number" min="2023" value={year} onChange={(e) => setYear(e.target.value)} required />
            </label>
            {/* Start Date */}
            <label>
                Start Date:
                <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} required />
            </label>
            {/* End Date */}
            <label>
                End Date:
                <input type="date" value={endDate} min={startDate} onChange={(e) => setEndDate(e.target.value)} required />
            </label>
            {/* Select Resort Name */}
            <label>
                Select Resort Name:
                <select value={resortName} onChange={(e) => setResortName(e.target.value)} required>
                    <option value="">Select a resort</option>
                    {resortList.map((resort, index) => (
                        <option key={index} value={resort.Resort}>{resort.Resort}</option>
                    ))}
                </select>
            </label>
            {/* Notes */}
            <label>
                Notes:
                <textarea value={note} onChange={(e) => setNote(e.target.value)}></textarea>
            </label>
            {/* Submit Button */}
            <button type="submit">Submit</button>
        </form>
    );
};
