import React, { useState, useEffect, useRef, forwardRef } from 'react';
import { Link } from 'react-router-dom';

const Checkbox = forwardRef(({ label, checked, isIndeterminate, onChange }, ref) => {
    useEffect(() => {
        if (ref.current) {
            ref.current.indeterminate = isIndeterminate;
        }
    }, [isIndeterminate, ref]);

    return (
        <label>
            <input
                type="checkbox"
                ref={ref}
                checked={checked}
                onChange={(event) => onChange(event.target.checked)}
            />
            {label}
        </label>
    );
});

export default function ResortManagement() {
    const [savedResorts, setSavedResorts] = useState([]);
    const [selectedSavedResorts, setSelectedSavedResorts] = useState(new Set());
    const selectAllSavedRef = useRef(null);

    const [reviews, setReviews] = useState([]);
    const [trips, setTrips] = useState([]);

    useEffect(() => {
        fetch('http://localhost:3000/saved')
            .then(response => response.json())
            .then(data => setSavedResorts(data))
            .catch(error => console.error('Error:', error));

        fetch('http://localhost:3000/reviews')
            .then(response => response.json())
            .then(data => setReviews(data))
            .catch(error => console.error('Error:', error));

        fetch('http://localhost:3000/trips')
            .then(response => response.json())
            .then(data => setTrips(data))
            .catch(error => console.error('Error:', error));
    }, []);

    useEffect(() => {
        if (selectAllSavedRef.current) {
            selectAllSavedRef.current.indeterminate =
                selectedSavedResorts.size > 0 && selectedSavedResorts.size < savedResorts.length;
        }
    }, [selectedSavedResorts, savedResorts]);

    const handleSelectAllSaved = (isChecked) => {
        setSelectedSavedResorts(new Set(isChecked ? savedResorts.map(r => r.id) : []));
    };

    const handleSelectSavedResort = (id, isChecked) => {
        const newSelected = new Set(selectedSavedResorts);
        if (isChecked) {
            newSelected.add(id);
        } else {
            newSelected.delete(id);
        }
        setSelectedSavedResorts(newSelected);
    };

    const handleDeleteSelected = () => {
        Promise.all(Array.from(selectedSavedResorts).map(id =>
            fetch(`http://localhost:3000/saved/${id}`, { method: 'DELETE' })
        ))
            .then(() => {
                setSavedResorts(savedResorts.filter(r => !selectedSavedResorts.has(r.id)));
                setSelectedSavedResorts(new Set());
                alert('Selected resorts have been deleted');
            })
            .catch(error => console.error('Error:', error));
    };

    return (
        <div>
            <h1>Resort Management</h1>

            <h2>Saved Resorts</h2>
            <Checkbox
                label="Select All"
                checked={selectedSavedResorts.size === savedResorts.length}
                isIndeterminate={selectedSavedResorts.size > 0 && selectedSavedResorts.size < savedResorts.length}
                onChange={handleSelectAllSaved}
                ref={selectAllSavedRef}
            />
            {savedResorts.map(resort => (
                <div key={resort.id}>
                    {resort.Resort} - <Link to={`/resort-details/${resort.Resort}`}>Detail</Link>
                    <input
                        type="checkbox"
                        checked={selectedSavedResorts.has(resort.id)}
                        onChange={(e) => handleSelectSavedResort(resort.id, e.target.checked)}
                    />
                </div>
            ))}
            {selectedSavedResorts.size > 0 && (
                <button onClick={handleDeleteSelected}>Delete Selected</button>
            )}

            <h2>Reviews</h2>
            {reviews.map(review => (
                <div key={review.id}>
                    {/* Render review details */}
                </div>
            ))}

            <h2>Trips</h2>
            {trips.map(trip => (
                <div key={trip.id}>
                    {/* Render trip details */}
                </div>
            ))}
        </div>
    );
}
