import React from 'react';
import { useState } from 'react';
import { useEffect, useLoaderData } from 'react-router-dom';
import Comments from "./comment";

export default function Planning({ resortList }) {
    const [suggestions, setSuggestions] = useState([]);

    const handleResortNameChange = (e) => {
        const value = e.target.value;
        setResortName(value);

        if (value.length > 0) {
            const regex = new RegExp(`^${value}`, 'i');
            const filteredSuggestions = resortList.filter(resort => regex.test(resort.Resort));
            setSuggestions(filteredSuggestions);
        } else {
            setSuggestions([]);
        }
    };

    const [year, setYear] = useState('2023');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [resortName, setResortName] = useState('');
    const [pass, setPass] = useState('');
    const [note, setNote] = useState('');

    const handleTripSubmit = (newTrip) => {
        fetch('http://localhost:3000/trips', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(tripData),
        })
            .then(response => response.json())
            .then(data => console.log('Trip added:', data))
            .catch(error => console.error('Error adding trip:', error));
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        const newTrip = {
            year,
            "date-start": startDate,
            "date-end": endDate,
            resortName,
            pass,
            note
        };

        handleTripSubmit(newTrip);

        // Reset form fields
        setYear('2023');
        setStartDate('');
        setEndDate('');
        setResortName('');
        setPass('');
        setNote('');
    };

    return (
        <form onSubmit={handleSubmit}>
            {/* Year Field */}
            <div>
                <label>
                    Year:
                    <input type="number" value={year} onChange={(e) => setYear(e.target.value)} min="2023" />
                </label>
            </div>

            {/* Start Date Field */}
            <div>
                <label>
                    Start Date:
                    <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
                </label>
            </div>

            {/* End Date Field */}
            <div>
                <label>
                    End Date:
                    <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
                </label>
            </div>

            {/* Resort Name Field with Auto-suggest */}
            <div>
                <label>
                    Resort Name:
                    <input type="text" value={resortName} onChange={handleResortNameChange} />
                    <div className="suggestions">
                        {suggestions && suggestions.map((suggestion, index) => (
                            <div key={index} className="suggestion-item" onClick={() => setResortName(suggestion.Resort)}>
                                {suggestion.Resort}
                            </div>
                        ))}
                    </div>
                </label>
            </div>

            {/* Pass Field */}
            <div>
                <label>
                    Pass:
                    <input type="text" value={pass} onChange={(e) => setPass(e.target.value)} />
                </label>
            </div>

            {/* Notes Field */}
            <div>
                <label>
                    Notes:
                    <textarea value={note} onChange={(e) => setNote(e.target.value)}></textarea>
                </label>
            </div>

            {/* Submit Button */}
            <button type="submit">Submit Trip</button>
        </form>
    );
};
