import React, { useState, useEffect } from 'react';

export default function saved({ resortName }) {
    const [resort, setResort] = useState(null);
    const [isSaved, setIsSaved] = useState(false);

    useEffect(() => {
        // Fetch the resort details
        fetch(`http://localhost:3000/resorts/${Resort}`)
            .then(response => response.json())
            .then(data => setResort(data))
            .catch(error => console.error('Error:', error));

        // Check if the resort is already saved
        fetch(`http://localhost:3000/saved?resortName=${resortName}`)
            .then(response => response.json())
            .then(data => setIsSaved(data.length > 0))
            .catch(error => console.error('Error:', error));
    }, [resortName]);

    const handleSave = () => {
        if (isSaved) {
            // Unsave the resort
            fetch(`http://localhost:3000/saved?resortName=${resortName}`, { method: 'DELETE' })
                .then(() => setIsSaved(false))
                .catch(error => console.error('Error:', error));
        } else {
            // Save the resort
            fetch('http://localhost:3000/saved', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ resortName, ...resort })
            })
                .then(() => setIsSaved(true))
                .catch(error => console.error('Error:', error));
        }
    };

    if (!resort) return <p>Loading...</p>;

    return (
        <div>
            <h1>{resort.resortName}</h1>
            {/* Display resort details */}
            <button onClick={handleSave}>
                {isSaved ? 'Unsave' : 'Save'}
            </button>
        </div>
    );
};
