import { createBrowserRouter, RouterProvider } from 'react-router-dom';
import React from 'react';
import ReactDOM from 'react-dom/client';

// Importing route components
import Root from './routes/root';
import DetailPage from './routes/details';
import FAQPagel from './routes/userFAQ';
import Index from './routes/index';
import Search from './routes/search';

import './css/index.css';

/*
ITP 404, Midterm
Author: Carson Wang
Email: carsonw@usc.edu
Create router, loader for react-router-dom
*/

// Define the loader function as async
const loader = async () => {
  const url = 'https://ski-resorts-and-conditions.p.rapidapi.com/v1/resort';
  const options = {
    method: 'GET',
    headers: {
      'X-RapidAPI-Key': '592da488fbmshc169f1ceb9ad9cbp19b7cbjsn897b69e46a35',
      'X-RapidAPI-Host': 'ski-resorts-and-conditions.p.rapidapi.com'
    }
  };

  try {
    const response = await fetch(url, options);
    if (!response.ok) {
      throw new Error('Network response was not ok');
    }
    const data = await response.json();
    console.log(data);
    return data; // Return the JSON data
  } catch (error) {
    console.error(error);
    return null; // Return null or throw an error depending on your error handling strategy
  }
};


// Create the router with route configurations
const router = createBrowserRouter([
  {
    path: '/',
    element: <Root />,
    children: [
      {
        path: '/',
        element: <Index />,
      },
      {
        path: '/SkiResorts',
        element: <DetailPage />,
        loader: loader,
      },
    ]
  }
]);

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <RouterProvider router={router} />
  </React.StrictMode>
);
