import React from 'react';
import { useState } from 'react';
import { useLoaderData, Link } from 'react-router-dom';

/*
ITP 404, Midterm
Author: Carson Wang
Email: carsonw@usc.edu
This file is for the home page which shows user list of meals, and allow user to conduct search function.
*/

export default function heroSection() {
  const [email, setEmail] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Here you would typically send the email to your server or a third-party email service.
    try {
      const response = await fetch('/api/subscribe', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });

      if (response.ok) {
        // Clear the form or give a success message
        setEmail('');
        alert('Thank you for subscribing!');
      } else {
        // Handle errors
        alert('There was an issue with your subscription. Please try again.');
      }
    } catch (error) {
      // Handle network errors
      alert('There was a problem connecting to the server.');
    }
  };
  return (
    <div>
      <div class="hero-section">
        <img src='/assets/hero.jpg' alt="Ski Resort Image" class="hero-image" />
        <div class="hero-text-box">
          <h1>Discover Your Perfect Ski Adventure</h1>
          <p>Find the latest snow conditions, lift statuses, and more.</p>
          <a href="#resorts" class="hero-cta-button">Explore Resorts</a>
        </div>
      </div>
      <div class="resort-overview-section">
        <h2>Featured Resorts</h2>
        <div class="resort-cards-container">
          <div class="resort-card">
            <img src="/assets/mammoth.jpeg" alt="Resort Name" class="resort-image" />
            <div class="resort-details">
              <h3>Mammoth Moutain Resort</h3>
              <p><strong>Snow Depth:</strong> 120cm</p>
              <p><strong>Open Lifts:</strong> 22 of 25</p>
              <p><strong>Weather:</strong> Sunny</p>
              <a href="link-to-detailed-resort-page" class="view-more">View More</a>
            </div>
          </div>
          <div class="resort-card">
            <img src="/assets/mammoth.jpeg" alt="Resort Name" class="resort-image" />
            <div class="resort-details">
              <h3>Mammoth Moutain Resort</h3>
              <p><strong>Snow Depth:</strong> 120cm</p>
              <p><strong>Open Lifts:</strong> 22 of 25</p>
              <p><strong>Weather:</strong> Sunny</p>
              <a href="link-to-detailed-resort-page" class="view-more">View More</a>
            </div>
          </div>
          <div class="resort-card">
            <img src="/assets/mammoth.jpeg" alt="Resort Name" class="resort-image" />
            <div class="resort-details">
              <h3>Mammoth Moutain Resort</h3>
              <p><strong>Snow Depth:</strong> 120cm</p>
              <p><strong>Open Lifts:</strong> 22 of 25</p>
              <p><strong>Weather:</strong> Sunny</p>
              <a href="link-to-detailed-resort-page" class="view-more">View More</a>
            </div>
          </div>
          <div class="resort-card">
            <img src="/assets/mammoth.jpeg" alt="Resort Name" class="resort-image" />
            <div class="resort-details">
              <h3>Mammoth Moutain Resort</h3>
              <p><strong>Snow Depth:</strong> 120cm</p>
              <p><strong>Open Lifts:</strong> 22 of 25</p>
              <p><strong>Weather:</strong> Sunny</p>
              <a href="link-to-detailed-resort-page" class="view-more">View More</a>
            </div>
          </div>
        </div>
      </div>
      <div className="newsletter-sign-up">
        <form onSubmit={handleSubmit}>
          <label htmlFor="email">Subscribe to our Newsletter:</label>
          <input
            type="email"
            id="email"
            name="email"
            placeholder="Enter your email address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <button type="submit">Subscribe</button>
        </form>
      </div>
    </div>
  );
}
