import React, { useState, useEffect } from 'react';
import { useLoaderData } from 'react-router-dom';

export default function Details() {
  // Fetch the resort data using react-router's useLoaderData hook
  const resortData = useLoaderData();

  // Initialize the resorts state to an empty array
  const [resorts, setResorts] = useState([]);

  return (
    <div className="page">
      <h1>Ski Resorts</h1>
      <ul>
        {resortData.map((resort) => (
          <li key={resort.slug}>
            <h2>{resort.name}</h2>
            <p>{resort.region}, {resort.country}</p>
            <a href={resort.url} target="_blank" rel="noopener noreferrer">View More</a>
          </li>
        ))}
      </ul>
    </div>
  );
}
