import React, { useState } from 'react';
import { useLoaderData, Link } from 'react-router-dom';
import TripForm from './planning';
import Collapsible from './collapsible.js';

export default function Planning() {
    const tripDetails = useLoaderData();
    const [trips, setTrips] = useState(tripDetails);
    const [selectedYear, setSelectedYear] = useState('');
    const [years, setYears] = useState([...new Set(tripDetails.map(trip => trip.id_year))]);

    const filteredTrips = trips.filter(trip => selectedYear === '' || trip.id_year === selectedYear);

    const handleDeleteYear = (yearId) => {
        fetch(`http://localhost:3000/trips/${yearId}`, {
            method: 'DELETE',
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                setTrips(trips.filter(trip => trip.id !== yearId));
            })
            .catch(error => console.error('Error deleting year:', error));
    };

    const handleDeleteTrip = (yearId, tripIndex) => {
        fetch(`http://localhost:3000/trips/${yearId}`)
            .then(response => response.json())
            .then(data => {
                const updatedDetails = data.details.filter((_, index) => index !== tripIndex);
                return fetch(`http://localhost:3000/trips/${yearId}`, {
                    method: 'PUT',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ ...data, details: updatedDetails }),
                });
            })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                setTrips(trips.map(trip =>
                    trip.id === yearId ? { ...trip, details: trip.details.filter((_, index) => index !== tripIndex) } : trip
                ));
            })
            .catch(error => console.error('Error deleting trip:', error));
    };

    const width = "30rem";

    return (
        <div className='page'>
            <h4>My Trips</h4>
            <Collapsible
                header="Need add another trip? (click + button)"
                renderTriggerIcon={(isOpen) => isOpen ? <span className='icon'>-</span> : <span className='icon'>+</span>}
            >
                <TripForm />
            </Collapsible>

            <hr />

            <div class="form-row">
                <div class="form-group col-md-4">
                    Select Year:
                    <select class="form-select" aria-label="Default select example" value={selectedYear} onChange={(e) => setSelectedYear(e.target.value)}>
                        <option value="">All Years</option>
                        {years.map((year, index) => (
                            <option key={index} value={year}>{year}</option>
                        ))}
                    </select>
                </div>
            </div>

            {
                filteredTrips.length > 0 ? (
                    filteredTrips.map((trip, index) => (
                        <div key={index}>
                            <h2>Year: {trip.id_year}</h2>
                            <button type="submit" class="btn btn-primary mb-3" onClick={() => handleDeleteYear(trip.id)}>Delete This Year</button>
                            {trip.details.sort((a, b) => new Date(a["date-start"]) - new Date(b["date-start"])).map((detail, detailIndex) => (
                                <div key={detailIndex}>
                                    <div class="card" style={{ width: width }}>
                                        <div class="card-body">
                                            <h5 class="card-title">Trip to {detail.resortName}</h5>
                                            <h6 class="card-subtitle mb-2 text-muted">Date Start: {detail["date-start"]}</h6>
                                            <h6 class="card-subtitle mb-2 text-muted">End Start: {detail["date-end"]}</h6>
                                            <h6 class="card-subtitle mb-2 text-muted">Pass: {detail.pass}</h6>
                                            <p class="card-text">Notes: {detail.note}</p>
                                            <a class="card-link"><Link to={`/resort-details/${detail.resortName}`}>Resort Details</Link></a>
                                            <a class="card-link"><button type="submit" class="btn btn-primary mb-3" onClick={() => handleDeleteTrip(trip.id, detailIndex)}>Delete This Trip</button></a>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    ))
                ) : (
                    <p>No trips available for the selected year.</p>
                )
            }
        </div >
    );
}
