import React, { useState, useEffect } from 'react';

export default function Comments({ resortName }) {
  const [reviews, setReviews] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:3000/reviews?resortName=${encodeURIComponent(resortName)}`)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setReviews(data);
        setIsLoading(false);
      })
      .catch(error => {
        console.error('There has been a problem with your fetch operation:', error);
        setIsLoading(false);
      });
  }, [resortName]);

  const sortedReviews = reviews.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));

  function onSubmit() {
    fetch("http://localhost:3000/reviews", {
      method: "POST",
      body: JSON.stringify({
        "comment": "asldf;k;afslfdkjals"
      }),
      headers: {
        "Content-type": "application/json",
      },
    })
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        console.log(json);
      });
  }

  return (
    <div>
      <h2>Reviews for {resortName}</h2>
      {isLoading ? (
        <p>Loading reviews...</p>
      ) : (
        sortedReviews.length > 0 ? (
          <ul>
            {sortedReviews.map((review, index) => (
              <li key={index}>
                <p><strong>{review.author}</strong> ({new Date(review.timestamp).toLocaleDateString()}):</p>
                <p>{review.comment}</p>
                <p>Rating: {review.rating} / 5</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No reviews available for this resort.</p>
        )
      )}
      <form onSubmit={onSubmit()}>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
