import React from 'react';
import { useLoaderData } from 'react-router-dom';

export default function Details() {
  // Fetch the resort data using react-router's useLoaderData hook
  const resortDetails = useLoaderData();

  // Assuming resortDetails contains all the information of the resort
  return (
    <div className="resort-details">
      <h2>{resortDetails.Resort}
        {resortDetails["Ski Pass"].toLowerCase() === "ikon" ? (
          <img className="passimg" src="/assets/ikon.png" alt="Ikon Pass" />
        ) : (
          <img className="passimg" src="/assets/epic.png" alt="Other Ski Pass" />
        )}</h2>
      <table class="table table-bordered border-primary">
        <tr>
          <th>Ski Pass:</th>
          <td>{resortDetails["Ski Pass"]}</td>
        </tr>
        <tr>
          <th>Location:</th>
          <td>{resortDetails["Location (State)"]}</td>
        </tr>
        <tr>
          <th>Days Open This Season:</th>
          <td>{resortDetails["Days Open This Season"]}</td>
        </tr>
      </table>
    </div>
  );
}
