import React, { useState, useEffect } from 'react';

export default function Saved({ resortName }) {
    const [resort, setResort] = useState(null);
    const [isSaved, setIsSaved] = useState(false);

    useEffect(() => {
        // Fetch the resort details
        fetch(`http://localhost:3000/resorts?Resort=${encodeURIComponent(resortName)}`)
            .then(response => response.json())
            .then(data => {
                if (data.length > 0) {
                    setResort(data[0]);
                }
            })
            .catch(error => console.error('Error:', error));

        // Check if the resort is already saved
        fetch(`http://localhost:3000/saved?resortName=${encodeURIComponent(resortName)}`)
            .then(response => response.json())
            .then(data => setIsSaved(data.length > 0))
            .catch(error => console.error('Error:', error));
    }, [resortName]);

    const handleSave = () => {
        if (isSaved) {
            // Unsave the resort
            fetch(`http://localhost:3000/saved?resortName=${encodeURIComponent(resortName)}`)
                .then(response => response.json())
                .then(savedItems => {
                    if (savedItems.length > 0) {
                        return fetch(`http://localhost:3000/saved/${savedItems[0].id}`, { method: 'DELETE' });
                    }
                    throw new Error('Item not found for deletion.');
                })
                .then(() => setIsSaved(false))
                .catch(error => console.error('Error:', error));
        } else {
            // Save the resort
            const resortToSave = {
                ...resort,
                resortName // Assuming you want to save this separately
            };

            fetch('http://localhost:3000/saved', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(resortToSave)
            })
                .then(() => setIsSaved(true))
                .catch(error => console.error('Error:', error));
        }
    };

    if (!resort) return <p>Loading...</p>;

    return (
        <div>
            <h1>{resortName}</h1>
            {/* Display resort details here */}
            <button onClick={handleSave}>
                {isSaved ? 'Unsave' : 'Save'}
            </button>
        </div>
    );
}
