import React, { useState } from 'react';

function TripForm() {
    const [year, setYear] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [resortName, setResortName] = useState('');
    const [pass, setPass] = useState('');
    const [note, setNote] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    return (
        <form onSubmit={fetch(`http://localhost:3000/trips`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                "date-start": "2023-12-03",
                "date-end": "2023-12-06",
                "resortName": "Keystone",
                "pass": "Epic",
                "note": "Need to prepare flight ticket from lax to Colorado."
            }
            )
        })}>
            {/* Year Field */}
            <div>
                <label>
                    Year:
                    <input
                        type="number"
                        value={year}
                        onChange={(e) => setYear(e.target.value)}
                        min="2023"
                        required
                    />
                </label>
            </div>

            {/* Start Date Field */}
            <div>
                <label>
                    Start Date:
                    <input
                        type="date"
                        value={startDate}
                        onChange={(e) => setStartDate(e.target.value)}
                        required
                    />
                </label>
            </div>

            {/* End Date Field */}
            <div>
                <label>
                    End Date:
                    <input
                        type="date"
                        value={endDate}
                        onChange={(e) => setEndDate(e.target.value)}
                        required
                    />
                </label>
            </div>

            {/* Resort Name Field */}
            <div>
                <label>
                    Resort Name:
                    <input
                        type="text"
                        value={resortName}
                        onChange={(e) => setResortName(e.target.value)}
                        required
                    />
                </label>
            </div>

            {/* Pass Field */}
            <div>
                <label>
                    Pass:
                    <input
                        type="text"
                        value={pass}
                        onChange={(e) => setPass(e.target.value)}
                    />
                </label>
            </div>

            {/* Notes Field */}
            <div>
                <label>
                    Notes:
                    <textarea
                        value={note}
                        onChange={(e) => setNote(e.target.value)}
                    ></textarea>
                </label>
            </div>

            {/* Submit Button */}
            <button type="submit" disabled={loading}>Submit</button>

            {/* Error Message */}
            {error && <p>{error}</p>}
        </form>
    );
}

export default TripForm;
