import React from 'react';
import { useState } from 'react';
import { useLoaderData, Link } from 'react-router-dom';

/*
ITP 404, Final Project
Author: Carson Wang
Email: carsonw@usc.edu
This file is for the home page which shows user list of meals, and allow user to conduct search function.
*/

export default function mainPage() {
  const resortList = useLoaderData();
  const [searchResort, setSearchResort] = useState(resortList);
  return (
    <div className="page">
      <div className="title">
        <h1>Ski Resorts</h1>
      </div>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Search for a resort..."
          onChange={(event) => {
            const filteredResorts = resortList.filter((resort) =>
              resort.Resort.toLowerCase().includes(event.target.value.toLowerCase())
            );
            setSearchResort(filteredResorts);
          }}
        />
      </div>

      <div className="resort-list">
        {/* Mapping through the data to display the name of each resort. */}
        {searchResort.map((resort, index) => {
          return (
            <ul key={index}>
              <li>
                <Link to={`/resort-details/${resort.slug}`}>{resort.Resort}</Link>
                <p>Ski Pass: {resort["Ski Pass"]}</p>
                <p>Location: {resort["Location (State)"]}</p>
              </li>
            </ul>
          );
        })}
      </div>
    </div>
  );
}
