import React, { useState, useEffect } from 'react';
import { useLoaderData, Link } from 'react-router-dom';

/*
ITP 404, Final Project
Author: Carson Wang
Email: carsonw@usc.edu
This file is for the home page which shows user list of meals, and allow user to conduct search function.
*/

export default function mainPage() {
  const resortList = useLoaderData();
  const [searchResort, setSearchResort] = useState(resortList);
  const [displayedResorts, setDisplayedResorts] = useState([]);

  // Function to get 5 random resorts
  const getRandomResorts = (resorts, num = 5) => {
    const shuffled = [...resorts].sort(() => 0.5 - Math.random());
    return shuffled.slice(0, num);
  };

  useEffect(() => {
    // Initially set to 5 random resorts
    setDisplayedResorts(getRandomResorts(resortList));
  }, []); // Empty dependency array ensures this runs once on mount



  const handleSearch = (event) => {
    const term = event.target.value;
    setSearchResort(term);

    if (term) {
      const filteredResorts = resortList.filter((resort) =>
        resort.Resort.toLowerCase().includes(term.toLowerCase())
      );
      setDisplayedResorts(filteredResorts);
    } else {
      setDisplayedResorts(getRandomResorts(resortList)); // Reset to 5 random resorts when search term is cleared
    }
  };

  return (
    <div className="page">
      <div className="title">
        <h1>Ski Resorts</h1>
      </div>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Search for a resort..."
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>

      <div className="resort-list">
        {/* Mapping through the data to display the name of each resort. */}
        {displayedResorts.map((resort, index) => (
          <ul key={index}>
            <li>
              <Link to={`/resort-details/${resort.slug}`}>{resort.Resort}</Link>
              <p>Ski Pass: {resort["Ski Pass"]}</p>
              <p>Location: {resort["Location (State)"]}</p>
            </li>
          </ul>
        ))}
      </div>
    </div>
  );
}
