import React from 'react';
import { useState, useEffect } from 'react';
import { useLoaderData, Link } from 'react-router-dom';

/*
ITP 404, Midterm
Author: Carson Wang
Email: carsonw@usc.edu
This file is for the home page which shows user list of meals, and allow user to conduct search function.
*/

export default function heroSection() {
  const [scrollPosition, setScrollPosition] = useState(0);

  const handleScroll = () => {
    const position = window.pageYOffset;
    setScrollPosition(position);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Logic to determine color based on scroll position
  const scrollArrowColor = () => {
    if (scrollPosition < 50) {
      return '#ffffff'; // White before scrolling
    } else {
      return '#fca311'; // Orange after scrolling
    }
  };

  return (
    <div class="hero-section">
      <img src='/assets/hero.jpg' alt="Ski Resort Image" class="hero-image" />
      <div class="hero-text-box">
        <h1>Discover Your Perfect Ski Adventure</h1>
        <p>Find the latest snow conditions, lift statuses, and more.</p>
        <a href="#resorts" class="hero-cta-button">Explore Resorts</a>
      </div>
      <div className="scroll-indicator">
        <span className="scroll-arrow" style={{ color: scrollArrowColor() }}>â†“</span>
      </div>
    </div>
  );
}
