import { createBrowserRouter, RouterProvider } from 'react-router-dom';
import React from 'react';
import ReactDOM from 'react-dom/client';

// Importing route components
import Root from './routes/root';
import DetailPage from './routes/details';
import FAQPagel from './routes/userFAQ';
import Index from './routes/index';
import Search from './routes/search';

import './css/index.css';

/*
ITP 404, Midterm
Author: Carson Wang
Email: carsonw@usc.edu
Create router, loader for react-router-dom
*/

function fetch() {
  const data = null;

  const xhr = new XMLHttpRequest();
  xhr.withCredentials = true;

  xhr.addEventListener('readystatechange', function () {
    if (this.readyState === this.DONE) {
      console.log(this.responseText);
      return (this.responseText)
    }
  });

  xhr.open('GET', 'https://ski-resorts-and-conditions.p.rapidapi.com/v1/resort/whistler-blackcomb');
  xhr.setRequestHeader('X-RapidAPI-Key', '55989d32a6mshcc21389f5a6e365p1f7963jsn4be692249f51');
  xhr.setRequestHeader('X-RapidAPI-Host', 'ski-resorts-and-conditions.p.rapidapi.com');

  xhr.send(data);
  return data.json();
}


// Create the router with route configurations
const router = createBrowserRouter([
  {
    path: '/',
    element: <Root />,
    children: [
      {
        path: '/',
        element: <Index />,
      },
      {
        path: '/SkiResorts',
        element: <DetailPage />,
        loader() {
          // fill the loader below
          return fetch();
        },
      },
    ]
  }
]);

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <RouterProvider router={router} />
  </React.StrictMode>
);
