import React from 'react';
import { useState } from 'react';
import { useLoaderData, Link } from 'react-router-dom';

/*
ITP 404, Midterm
Author: Carson Wang
Email: carsonw@usc.edu
This file is for the home page which shows user list of meals, and allow user to conduct search function.
*/

export default function mainPage() {
  // Load data for list of meal using the useLoaderData hook
  const mealList = useLoaderData();
  // create useState() for user search function
  const [searchMeal, setSearchMeal] = useState(mealList);

  return (
    <div className="page">
      <div className="title">
        <h1>Meals</h1>
      </div>

      <div className="search-bar">
        <input
          type="text"
          onChange={(update) => {
            const filteredMeals = mealList.filter((meal) =>
              meal.name
                .toLowerCase()
                .includes(update.target.value.toLowerCase())
            );
            setSearchMeal(filteredMeals);
          }}
        />
      </div>

      <div className="meal-list">
        {/* Mapping through the data to display the name of each meals. */}
        {searchMeal.map((meals) => {
          return (
            <ul>
              {/* details of the specific meals */}
              <li>
                <Link to={`/leafside-meals/${meals.slug}`}>{meals.name}</Link>
              </li>
            </ul>
          );
        })}
      </div>
    </div>
  );
}
