import React from 'react';
import { useLoaderData } from 'react-router-dom';
import Comments from "./comment";

export default function Planning() {
    const tripDetails = useLoaderData();
    const [trips, setTrips] = useState(tripDetails);
    const [selectedYear, setSelectedYear] = useState('');
    const [years, setYears] = useState(tripDetails.map(trip => trip.id_year));

    const filteredTrips = trips.filter(trip => trip.id_year === selectedYear);

    return (
        <div>
            <h1>My Trips</h1>
            <label>
                Select Year:
                <select value={selectedYear} onChange={(e) => setSelectedYear(e.target.value)}>
                    <option value="">All Years</option>
                    {years.map((year, index) => (
                        <option key={index} value={year}>{year}</option>
                    ))}
                </select>
            </label>

            {filteredTrips.length > 0 ? (
                filteredTrips.map((trip, index) => (
                    <div key={index}>
                        <h2>Year: {trip.id_year}</h2>
                        {trip.details.map((detail, detailIndex) => (
                            <div key={detailIndex}>
                                <p>Date Start: {detail["date-start"]}</p>
                                <p>Date End: {detail["date-end"]}</p>
                                <p>Resort Name: <Link to={`/resorts/${detail.resortName}`}>{detail.resortName}</Link></p>
                                <p>Pass: {detail.pass}</p>
                                <p>Note: {detail.note}</p>
                            </div>
                        ))}
                    </div>
                ))
            ) : (
                <p>No trips available for the selected year.</p>
            )}
        </div>
    );
};
