import React from 'react';
import { useState } from 'react';
import { useLoaderData, Link } from 'react-router-dom';

/*
ITP 404, Midterm
Author: Carson Wang
Email: carsonw@usc.edu
This file is for the home page which shows user list of meals, and allow user to conduct search function.
*/

export default function heroSection() {
  return (
    <div class="hero-section">
      <img src='src/css/index.css' alt="Ski Resort Image" class="hero-image" />
      <div class="hero-text-box">
        <h1>Discover Your Perfect Ski Adventure</h1>
        <p>Find the latest snow conditions, lift statuses, and more.</p>
        <a href="#resorts" class="hero-cta-button">Explore Resorts</a>
      </div>
    </div>
  );
}
