import React from 'react';
import { useLoaderData, useNavigate, useParams } from 'react-router-dom';

export default function PlannedTrips() {
    const tripDetails = useLoaderData();
    const { resortName } = useParams();
    const navigate = useNavigate();

    const handleDeleteTrip = (yearId, tripIndex) => {
        fetch(`http://localhost:3000/trips/${yearId}`)
            .then(response => response.json())
            .then(data => {
                const updatedDetails = data.details.filter((_, index) => index !== tripIndex);
                return fetch(`http://localhost:3000/trips/${yearId}`, {
                    method: 'PUT',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ ...data, details: updatedDetails }),
                });
            })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                setTrips(trips.map(trip =>
                    trip.id === yearId ? { ...trip, details: trip.details.filter((_, index) => index !== tripIndex) } : trip
                ));
            })
            .catch(error => console.error('Error deleting trip:', error));
    };

    const hasTrips = tripDetails.some(trip => trip.details.length > 0);

    if (!hasTrips) {
        return (
            <div>
                <p>You have deleted all trips to {resortName}.</p>
                <button onClick={() => navigate('/my-trips')}>Back to My Trips</button>
            </div>
        );
    }

    return (
        <div>
            {tripDetails.map(trip => (
                <div key={trip.id_year}>
                    <h2>{trip.id_year}</h2>
                    {trip.details.map((detail, index) => (
                        <div key={index}>
                            <p>{detail["date-start"]} to {detail["date-end"]}</p>
                            <p>Pass: {detail.pass}</p>
                            <p>Note: {detail.note}</p>
                            <button onClick={() => handleDeleteTrip(trip.id_year, index)}>Delete</button>
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
}
