import { useState } from 'react';
import React from 'react';

/*
ITP 404, Midterm
Author: Carson Wang
Email: carsonw@usc.edu
This file display the common FAQs to user;
create faq array -> load using map -> answer should be viewable at a time
*/

export default function userFAQ() {
  // State to track the selected FAQ question
  const [userFAQ, setUserFAQ] = useState();

  // data for FAQs
  const listOfFAQ = [
    {
      question: 'Are LeafSide meals a form of processed food?',
      answer:
        "No and never, because every LeafSide meal is 100% whole foods plant-based (WFPB). As a company we've always been 100% WFPB since the beginning, and that will never change.",
    },
    {
      question: 'Where does the food come from?',
      answer:
        "We're currently based in Tempe, Arizona, and that's where our meal packs are made. The majority of our ingredients — organic and conventional — are from USA vendors, and in particular, all organic ingredients are USDA-certified organic.",
    },
    {
      question: 'How do I prepare the sweet bowls?',
      answer:
        'Mix with boiling water in a covered container and let it sit for 10 minutes.',
    },
  ];

  return (
    <div className="page">
      <div className="title">
        <h1>Common Questions</h1>
        <p>
          We're here to help you be more successful with a whole-foods
          plant-based lifestyle, and your questions are what drive us to improve
          and create amazing products.
        </p>
      </div>

      {/* Loop through each FAQ and display the question. If the question is selected, display its answer. */}
      {listOfFAQ.map((faq) => (
        <div key={faq.question}>
          {/* Button to toggle display of the FAQ answer */}
          <button
            type="button"
            onClick={() => {
              // Set the selected FAQ to the clicked FAQ. If it's already selected, deselect it.
              setUserFAQ(
                userFAQ === faq.question ? null : faq.question
              );
            }}
          >
            {faq.question}
          </button>

          {/* Display the FAQ answer if its question is selected */}
          {userFAQ === faq.question && (
            <p>{faq.answer}</p>
          )}
        </div>
      ))}

      <div className="listOfFAQ"></div>
    </div>
  );
}
