import React from 'react';
import { useState } from 'react';
import { useLoaderData, Link } from 'react-router-dom';

/*
ITP 404, Midterm
Author: Carson Wang
Email: carsonw@usc.edu
This file is for the home page which shows user list of meals, and allow user to conduct search function.
*/

export default function searchBar() {
  return (
    <div class="search-container">
      <input type="text" placeholder="Search for resorts, slopes, tips..." class="search-input" />
      <button class="search-button">Search</button>
    </div>
  );
}
